import axios from 'axios'
const BASE_URL = 'http://localhost:3002/api'

export function getToday() {
    const request = axios.get(`${BASE_URL}/keyword`, { keyword: 'Flamengo' })
    return {
        type: 'TODAY',
        payload: request
    }
}
